import React, { useState } from 'react';
import axios from 'axios';
import './styles/AdminPanel.css'
const AdminPanel = () => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: 'Фантастика',
    cover: '/default-cover.jpg',
    year: '',
    pages: '',
    price: '',
    inStock: true,
    rating: 0,
    description: '' // Добавьте это поле в схему если нужно
  });

  const [preview, setPreview] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/admin/books', formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('adminToken')}`
        }
      });
      alert('Книга успешно добавлена!');
      setFormData({
        title: '',
        author: '',
        genre: 'Фантастика',
        cover: '/default-cover.jpg',
        year: '',
        pages: '',
        price: '',
        inStock: true,
        rating: 0
      });
      setPreview('');
    } catch (error) {
      alert(`Ошибка: ${error.response?.data?.message || error.message}`);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));

    if (name === 'cover') {
      setPreview(value);
    }
  };

  return (
    <div className="admin-panel">
      <h2>Добавить новую книгу</h2>
      <form onSubmit={handleSubmit} className="book-form">
        {/* Превью обложки */}
        {preview && (
          <div className="cover-preview">
            <img src={preview} alt="Превью обложки" />
          </div>
        )}

        <div className="form-group">
          <label>Название:</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Автор:</label>
          <input
            type="text"
            name="author"
            value={formData.author}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Жанр:</label>
          <select
            name="genre"
            value={formData.genre}
            onChange={handleChange}
          >
            <option value="Фантастика">Фантастика</option>
            <option value="Детектив">Детектив</option>
            <option value="Роман">Роман</option>
            <option value="Научная литература">Научная литература</option>
          </select>
        </div>

        <div className="form-group">
          <label>Ссылка на обложку:</label>
          <input
            type="url"
            name="cover"
            value={formData.cover}
            onChange={handleChange}
            placeholder="https://example.com/cover.jpg"
          />
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Год издания:</label>
            <input
              type="number"
              name="year"
              value={formData.year}
              onChange={handleChange}
              min="1800"
              max={new Date().getFullYear()}
            />
          </div>

          <div className="form-group">
            <label>Количество страниц:</label>
            <input
              type="number"
              name="pages"
              value={formData.pages}
              onChange={handleChange}
              min="1"
            />
          </div>
        </div>

        <div className="form-row">
          <div className="form-group">
            <label>Цена:</label>
            <input
              type="number"
              name="price"
              value={formData.price}
              onChange={handleChange}
              min="0"
              step="0.01"
            />
          </div>

          <div className="form-group">
            <label>Рейтинг:</label>
            <input
              type="number"
              name="rating"
              value={formData.rating}
              onChange={handleChange}
              min="0"
              max="5"
              step="0.1"
            />
          </div>
        </div>

        <div className="form-group checkbox-group">
          <label>
            <input
              type="checkbox"
              name="inStock"
              checked={formData.inStock}
              onChange={handleChange}
            />
            В наличии
          </label>
        </div>

        <button type="submit" className="submit-btn">
          Добавить книгу
        </button>
      </form>
    </div>
  );
};

export default AdminPanel;
