// AdminPanel.jsx
import React, { useState } from 'react';
import axios from 'axios';
import '../styles/AdminPanel.css'
const AdminPanel = () => {
  const [formData, setFormData] = useState({
    title: '',
    author: '',
    genre: 'Фантастика',
    cover: '/default-cover.jpg',
    year: '',
    pages: '',
    price: '',
    inStock: true,
    rating: 0
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/books/create', formData);
      alert('Книга успешно добавлена!');
      // Сброс формы
      setFormData({ ...formData, title: '', author: '', year: '', pages: '', price: '' });
    } catch (error) {
      alert('Ошибка при добавлении книги: ' + error.response.data.message);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  return (
    <div className="admin-panel">
      <h2>Добавить новую книгу</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Название:</label>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Автор:</label>
          <input
            type="text"
            name="author"
            value={formData.author}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Жанр:</label>
          <select
            name="genre"
            value={formData.genre}
            onChange={handleChange}
          >
            <option value="Фантастика">Фантастика</option>
            <option value="Детектив">Детектив</option>
            <option value="Роман">Роман</option>
            <option value="Научная литература">Научная литература</option>
          </select>
        </div>

        {/* Добавьте остальные поля аналогичным образом */}

        <button type="submit">Добавить книгу</button>
      </form>
    </div>
  );
};

export default AdminPanel;
