import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Header.css'; // если есть стили
import defaultAvatar from '../assets/default-avatar.png'; // если есть дефолтная аватарка

const Header = () => {
  const [user, setUser] = useState(null);
  const [menuOpen, setMenuOpen] = useState(false);
  const navigate = useNavigate();
  const menuRef = useRef();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetch('http://localhost:5000/api/auth/me', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      })
        .then(res => res.json())
        .then(data => setUser(data))
        .catch(err => console.error('Ошибка загрузки пользователя:', err));
    }
  }, []);

  // Закрытие меню при клике вне
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setMenuOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
    setMenuOpen(false);
    navigate('/');
  };

  const getInitials = (name = '') => {
    return name
      .split(' ')
      .map(word => word[0])
      .join('')
      .toUpperCase();
  };

  const getRandomColor = () => {
    const colors = ['#60a5fa', '#f472b6', '#4ade80', '#facc15', '#c084fc'];
    return colors[Math.floor(Math.random() * colors.length)];
  };

  return (
    <header className="header">
      <h1 className="logo" onClick={() => navigate('/')}>BookStore</h1>

      {user ? (
        <div className="user-section" ref={menuRef}>
          <div
            className="avatar"
            onClick={() => setMenuOpen(!menuOpen)}
            style={{
              backgroundColor: user.avatar ? 'transparent' : getRandomColor(),
              backgroundImage: user.avatar ? `url(${user.avatar})` : 'none',
              backgroundSize: 'cover'
            }}
          >
            {!user.avatar && getInitials(user.name)}
          </div>

          {menuOpen && (
            <div className="dropdown-menu">
              <button onClick={() => { navigate('/profile'); setMenuOpen(false); }}>Профиль</button>
              <button onClick={handleLogout}>Выйти</button>
            </div>
          )}
        </div>
      ) : (
        <button className="login-button" onClick={() => navigate('/login')}>Войти</button>
      )}
    </header>
  );
};

export default Header;
